
public class RangeOfDatatypeEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	        System.out.println("Byte:- " + Byte.SIZE+"  "+Byte.MIN_VALUE+ " to " + Byte.MAX_VALUE);
	        System.out.println("Short:- " + Short.SIZE+ "  " + Short.MIN_VALUE + " to " + Short.MAX_VALUE);
	        System.out.println("Integer:- " + Integer.SIZE+ "  " + Integer.MIN_VALUE+ " to " + Integer.MAX_VALUE);
	        System.out.println("Float:- " + Float.SIZE+ "  " + Float.MIN_VALUE+" to " + Float.MAX_VALUE);
	        System.out.println("Long:- " + Long.SIZE+ "  " + Long.MIN_VALUE + " to "+ Long.MAX_VALUE);
	        System.out.println("Double:- " + Double.SIZE+ "  " + Double.MIN_VALUE+ " to " + Short.MAX_VALUE);
	}

}
